import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import userService from './../../services/userService';

import './RegisterVerify.css';

export default function RegisterAccountVerify(props) {
	const query = new URLSearchParams(window.location.search);
	const data = {
		userId: query.get('userId'),
		code: query.get('code'),
	};

	const [state, setState] = useState({
		isSuccessed: false,
		isLoading: true,
		errors: [],
	});

	useEffect(() => {
		userService
			.registerVerify(data)
			.then((res) => {
				if (res.isSuccessed) {
					setState({
						isSuccessed: true,
						isLoading: false,
						errors: [],
					});
				} else {
					setState({
						isSuccessed: false,
						isLoading: false,
						errors: [res.message],
					});
				}
			})
			.catch((error) => {
				setState({
					isSuccessed: false,
					isLoading: false,
					errors: [error.message],
				});
			});
	}, []);

	return (
		<div className="view-register-verify">
			<div className="view-register-verify__content">
				<h1>Trang xác nhận đăng ký tài khoản</h1>
				{state.isLoading && <p>Vui lòng đợi</p>}

				{state.errors.length > 0 &&
					state.errors.map((item, i) => <h4 key={i}>{item}</h4>)}

				{state.isSuccessed && (
					<div>
						<p>Bạn đã xác nhận thành công</p>
					</div>
				)}
				<Link to="/dang-nhap">Đi đến trang đăng nhập</Link>
			</div>
		</div>
	);
}
