import React, { useContext, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { Spinner } from 'reactstrap';

import LoginForm from './../../components/authenticate/LoginForm';
import { AuthContext } from './../../contexts/member/AuthContext';
import * as AUTH_TYPE from './../../reducers/member/authType';

import './Login.css';

export default function LoginAccount({ title }) {
	const { authState, dispatch } = useContext(AuthContext);

	useEffect(() => {
		document.title = title;

		dispatch({
			type: AUTH_TYPE.SET_AUTH,
			payload: null,
		});
	}, []);

	if (authState.authLoading) {
		return (
			<div
				className="d-flex justify-content-center mt-2"
				style={{ height: '100%' }}
			>
				<Spinner color="primary" />
			</div>
		);
	}

	if (authState.isAuthenticated) {
		return <Redirect to="/" />;
	}
	return (
		<div className="view-login">
			<div className="view-login__content">
				<div className="block text">
					<div className="login-form">
						<h1 className="text-center">Đăng nhập</h1>
						<div>
							<ul>
								{authState.errors &&
									authState.errors.map((item, i) => (
										<b key={i}>
											{item} <br />
										</b>
									))}
							</ul>

							<LoginForm />
						</div>
						<div className="foot-login row mt-4 w-100">
							<h6 className="col-6">
								<a href="javascript:void(0)">Quên mật khẩu?</a>
							</h6>
							<h6 className="col-6 text-right">
								<a href="javascript:void(0)">Đăng ký</a>
							</h6>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
}
