import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';

import { AccountRegisterContext } from './../../contexts/member/AccountRegisterContext';
import * as ACCOUNT_REGISTER_TYPE from './../../reducers/member/accountRegisterType';

import './RegisterForm.css';

const checkValidate = (user) => {
	const {
		fullname,
		email,
		dob,
		avatar,
		address,
		gender,
		phoneNumber,
		username,
		password,
		passwordConfirm,
	} = user;

	const errors = [];
	if (!fullname) errors.push('Tên không hợp lệ');
	if (!email) errors.push('Email không hợp lệ');
	if (!dob) errors.push('Ngày sinh không hợp lệ');
	if (!avatar) errors.push('Ảnh đại diện không hợp lệ');
	if (!address) errors.push('Địa chỉ không hợp lệ');
	if (!gender) errors.push('Giới tính không hợp lệ');
	if (!phoneNumber) errors.push('Số điện thoại không hợp lệ');
	if (!username) errors.push('Tài khoản không hợp lệ');
	if (!password) errors.push('Mật khẩu không hợp lệ');
	if (!passwordConfirm) {
		errors.push('Nhập lại mật khẩu không hợp lệ');
	} else if (password !== passwordConfirm) {
		errors.push('Nhập lại mật khẩu không trùng khớp');
	}

	return errors;
};

export default function RegisterForm() {
	const [user, setUser] = useState({
		fullname: '',
		email: '',
		dob: Date.now(),
		avatar: '',
		address: '',
		gender: 'male',
		phoneNumber: '',
		username: '',
		password: '',
		passwordConfirm: '',
	});
	const { dispatch } = useContext(AccountRegisterContext);

	const isFemale = () => {
		if (user && user.gender === 'female') return true;

		return false;
	};

	const onChange = (e) => {
		setUser({ ...user, [e.target.name]: e.target.value });
	};

	const onChangeImage = (e) => {
		setUser({ ...user, [e.target.name]: e.target.files[0] }); // set nguyên files vì chọn nhiềus
	};

	const onSubmit = (e) => {
		e.preventDefault();

		const errors = checkValidate(user);

		dispatch({
			type: ACCOUNT_REGISTER_TYPE.SET_LOADING,
			payload: {},
		});

		dispatch({
			type: ACCOUNT_REGISTER_TYPE.SET_ERRORS,
			payload: { errors },
		});

		if (errors.length <= 0) {
			dispatch({
				type: ACCOUNT_REGISTER_TYPE.REGISTER,
				payload: { user },
			});
		}
	};

	return (
		<div className="regitser-form">
			<form
				action=""
				method=""
				onSubmit={(e) => onSubmit(e)}
				className="form"
			>
				<p>Họ tên</p>
				<input
					type="text"
					name="fullname"
					value={user.fullName}
					onChange={(e) => onChange(e)}
				/>
				<p>Email</p>
				<input
					type="email"
					name="email"
					value={user.email}
					onChange={(e) => onChange(e)}
				/>
				<p>Ngày sinh</p>
				<input
					type="date"
					name="dob"
					value={user.dob}
					onChange={(e) => onChange(e)}
				/>
				{/*  */}
				<p>Ảnh đại diện</p>

				<input
					type="file"
					name="avatar"
					onChange={(e) => onChangeImage(e)}
				/>
				{user.avatar && (
					<img
						src={URL.createObjectURL(user.avatar)}
						alt="Avatar"
						width="200"
						height="200"
						className="mt-2"
					/>
				)}

				<p>Địa chỉ</p>
				<input
					type="text"
					name="address"
					value={user.address}
					onChange={(e) => onChange(e)}
				/>

				<p>Giới tính</p>
				<div className="form-check">
					<input
						className="form-check-input"
						value="male"
						type="radio"
						name="gender"
						id="male"
						checked={`${isFemale() ? '' : 'checked'}`}
						onChange={(e) => onChange(e)}
					/>
					<label className="form-check-label" htmlFor="male">
						Nam
					</label>
				</div>
				<div className="form-check">
					<input
						className="form-check-input"
						value="female"
						type="radio"
						name="gender"
						id="female"
						checked={`${isFemale() ? 'checked' : ''}`}
						onChange={(e) => onChange(e)}
					/>
					<label className="form-check-label" htmlFor="female">
						Nữ
					</label>
				</div>
				<p>Số điện thoại</p>
				<input
					type="phone"
					name="phoneNumber"
					value={user.phoneNumber}
					onChange={(e) => onChange(e)}
				/>
				{/*  */}
				<p>Tài khoản</p>
				<input
					type="text"
					name="username"
					value={user.username}
					onChange={(e) => onChange(e)}
				/>
				<p className="mt-5">Mật khẩu</p>
				<input
					type="password"
					name="password"
					value={user.password}
					onChange={(e) => onChange(e)}
				/>
				<p className="mt-5">Nhập lại mật khẩu</p>
				<input
					type="password"
					name="passwordConfirm"
					value={user.passwordConfirmword}
					onChange={(e) => onChange(e)}
				/>
				<input
					type="submit"
					className="btn-login w-100"
					value="Đăng ký"
				></input>
			</form>
		</div>
	);
}
