import React, { useContext, useEffect } from 'react';
import { Redirect } from 'react-router-dom';

import { checkRoleIsValid } from './../utils/checkRoleIsValid';

import MemberHeader from './../components/partials/member/MemberHeader';

import { AuthContext } from './../contexts/member/AuthContext';
import * as AUTH_TYPE from './../reducers/member/authType';
import MemberFooter from '../components/partials/member/MemberFooter';

export default function MemberLayout({ children, roles, title }) {
	const { authState, dispatch } = useContext(AuthContext);

	document.title = title;

	useEffect(() => {
		dispatch({
			type: AUTH_TYPE.SET_AUTH,
			payload: null,
		});
	}, []);

	if (authState.authLoading) {
		return (
			<div className="d-flex justify-content-center align-items-center mt-2 my-loading">
				<div className="text-center">
					<h1>Loading...</h1>
				</div>
			</div>
		);
	}

	if (!checkRoleIsValid(roles, authState.permission))
		return <Redirect to="/error-401" />;

	return (
		<React.Fragment>
			<MemberHeader />
			<hr />
			<div>{children}</div>
			<MemberFooter />
		</React.Fragment>
	);
}
