import React, { useContext, useEffect } from 'react';
import { Redirect } from 'react-router-dom';

import RegisterForm from './../../components/accountRegister/RegisterForm';
import { AccountRegisterContext } from './../../contexts/member/AccountRegisterContext';
import { AuthContext } from './../../contexts/member/AuthContext';
import * as ACCOUNT_REGISTER_TYPE from './../../reducers/member/accountRegisterReducer';
import * as AUTH_TYPE from './../../reducers/member/authType';

import './Register.css';

export default function RegisterAccount({ title }) {
	const { accountRegisterState, dispatch } = useContext(
		AccountRegisterContext
	);
	const { authState, dispatch: authDispatch } = useContext(AuthContext);

	useEffect(() => {
		document.title = title;

		authDispatch({
			type: AUTH_TYPE.SET_AUTH,
			payload: null,
		});
	}, []);

	if (authState.isAuthenticated) {
		return <Redirect to="/" />;
	}
	return (
		<div className="view-register">
			<div className="view-register__content">
				<div className="block text">
					<div className="register-form">
						<h1 className="title text-center">Đăng ký</h1>
						{accountRegisterState.isSuccessed && (
							<p>
								Tạo tài khoản thành công, vui lòng kiểm tra lại email để
								xác nhận đăng ký
							</p>
						)}

						{accountRegisterState.isLoading && <span>Loading...</span>}
						<ul className="view-register__errors">
							{accountRegisterState.errors &&
								accountRegisterState.errors.map((item, i) => (
									<b key={i}>
										{item} <br />
									</b>
								))}
						</ul>

						<RegisterForm />
					</div>
				</div>
			</div>
		</div>
	);
}
