import helloworldService from '../../services/helloworldService';
import * as TYPE from './helloworldType';

export const helloworldReducer = async (state, action) => {
	const { type, payload } = action;

	switch (type) {
		case TYPE.SET: {
			const res = await helloworldService.get();

			return {
				data: res.result,
			};
		}

		default:
			return state;
	}
};
