import React, { useContext, useEffect } from 'react';

import { HelloworldContext } from './../../../contexts/member/HelloworldContext';
import * as HELLO_WORLD_TYPE from './../../../reducers/member/helloworldType';

const Helloworld = () => {
	const { helloworldState, dispatch } = useContext(HelloworldContext);
	console.log('hello world state', helloworldState);
	useEffect(() => {
		dispatch({
			type: HELLO_WORLD_TYPE.SET,
			payload: null,
		});
	}, []);

	return (
		<div className="view-member-helloworld">Xin chào {helloworldState}</div>
	);
};

export default Helloworld;
