import React, { createContext } from 'react';

import { helloworldReducer } from '../../reducers/member/helloworldReducer';
import useAsyncReducer from '../../reducers/useAsyncReducer';
import * as HELLO_WORLD_TYPE from '../../reducers/member/helloworldType';

export const HelloworldContext = createContext();

export default function HelloworldContextProvider({ children }) {
	const [helloworldState, dispatch] = useAsyncReducer(helloworldReducer, {
		data: '',
	});

	const helloworldContextData = {
		helloworldState,
		dispatch,
	};

	return (
		<HelloworldContext.Provider value={helloworldContextData}>
			{children}
		</HelloworldContext.Provider>
	);
}
