import React, { useContext, useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

import { AccountRegisterContext } from './../../contexts/member/AccountRegisterContext';
import * as ACCOUNT_REGISTER_TYPE from './../../reducers/member/accountRegisterType';

import './RegisterForm.css';

const checkValidate = (user) => {
	const {
		fullName,
		email,
		dob,
		avatar,
		address,
		gender,
		phoneNumber,
		username,
		password,
		passwordConfirm,
	} = user;

	const errors = [];
	if (!fullName) errors.push('Tên không hợp lệ');
	if (!email) errors.push('Email không hợp lệ');
	if (!dob) errors.push('Ngày sinh không hợp lệ');
	if (!avatar) errors.push('Ảnh đại diện không hợp lệ');
	if (!address) errors.push('Địa chỉ không hợp lệ');
	if (!gender) errors.push('Giới tính không hợp lệ');
	if (!phoneNumber) errors.push('Số điện thoại không hợp lệ');
	if (!username) errors.push('Tài khoản không hợp lệ');
	if (!password) errors.push('Mật khẩu không hợp lệ');
	if (!passwordConfirm) {
		errors.push('Nhập lại mật khẩu không hợp lệ');
	} else if (password !== passwordConfirm) {
		errors.push('Nhập lại mật khẩu không trùng khớp');
	}

	return errors;
};

export default function RegisterForm() {
	const [user, setUser] = useState({
		fullName: '',
		email: '',
		dob: '2000-01-01',
		avatar: '',
		address: '',
		gender: 'male',
		phoneNumber: '',
		username: '',
		password: '',
		passwordConfirm: '',
	});
	const [submitClicked, setSubmitClicked] = useState(false);

	const { accountRegisterState, dispatch } = useContext(
		AccountRegisterContext
	);
	const avatarRef = useRef();

	const isFemale = () => {
		if (user && user.gender === 'female') return true;

		return false;
	};

	const onChange = (e) => {
		setUser({ ...user, [e.target.name]: e.target.value });
	};

	const onChangeImage = (e) => {
		setUser({ ...user, [e.target.name]: e.target.files[0] }); // set nguyên files vì chọn nhiềus
	};

	const onSubmit = (e) => {
		e.preventDefault();
		setSubmitClicked(true);

		const errors = checkValidate(user);

		// dispatch({
		// 	type: ACCOUNT_REGISTER_TYPE.SET_LOADING,
		// 	payload: {},
		// });

		// dispatch({
		// 	type: ACCOUNT_REGISTER_TYPE.SET_ERRORS,
		// 	payload: { errors },
		// });

		if (errors.length <= 0) {
			dispatch({
				type: ACCOUNT_REGISTER_TYPE.REGISTER,
				payload: { user },
			});
		}
	};

	useEffect(() => {
		if (accountRegisterState.isSuccessed) {
			console.log('success');
			setUser({
				fullName: '',
				email: '',
				dob: '2000-01-01',
				avatar: '',
				address: '',
				gender: 'male',
				phoneNumber: '',
				username: '',
				password: '',
				passwordConfirm: '',
			});
			setSubmitClicked(false);
			avatarRef.current.value = '';
			console.log(avatarRef);
		}
	}, [accountRegisterState]);

	return (
		<div className="register-form">
			<form
				action=""
				method=""
				onSubmit={(e) => onSubmit(e)}
				className="register-form__form row"
			>
				<div className="col-6">
					<p className="input-text">Họ tên</p>
					<input
						type="text"
						className="color-transparent"
						name="fullName"
						value={user.fullName}
						onChange={(e) => onChange(e)}
					/>
					{!user.fullName && submitClicked && (
						<span className="error-item">Vui lòng nhập tên</span>
					)}
					<p className="input-text">Email</p>
					<input
						type="email"
						className="color-transparent"
						name="email"
						value={user.email}
						onChange={(e) => onChange(e)}
					/>
					{!user.email && submitClicked && (
						<span className="error-item">Vui lòng nhập email</span>
					)}
					<p className="input-text">Địa chỉ</p>
					<input
						type="text"
						className="color-transparent"
						name="address"
						value={user.address}
						onChange={(e) => onChange(e)}
					/>
					{!user.address && submitClicked && (
						<span className="error-item">Vui lòng nhập địa chỉ</span>
					)}
					<p className="input-text">Ngày sinh</p>
					<input
						type="date"
						className="color-transparent"
						name="dob"
						value={user.dob}
						min="1970-01-01"
						max="2020-12-31"
						onChange={(e) => onChange(e)}
					/>
					{!user.dob && submitClicked && (
						<span className="error-item">Vui lòng chọn ngày sinh</span>
					)}
					<p className="input-text">Ảnh đại diện</p>

					<input
						type="file"
						className="color-transparent"
						name="avatar"
						ref={avatarRef}
						onChange={(e) => onChangeImage(e)}
					/>
					{/* {user.avatar && (
						<img
							src={URL.createObjectURL(user.avatar)}
							alt="Avatar"
							width="200"
							height="200"
							className="mt-2"
						/>
					)} */}
					{!user.avatar && submitClicked && (
						<span className="error-item">Vui lòng chọn ảnh đại diện</span>
					)}
				</div>

				<div className="col-6">
					<p className="input-text">Số điện thoại</p>
					<input
						type="phone"
						className="color-transparent"
						name="phoneNumber"
						value={user.phoneNumber}
						onChange={(e) => onChange(e)}
					/>
					{!user.phoneNumber && submitClicked && (
						<span className="error-item">
							Vui lòng nhập số điện thoại
						</span>
					)}
					{/*  */}
					<p className="input-text">Tài khoản</p>
					<input
						type="text"
						className="color-transparent"
						name="username"
						value={user.username}
						onChange={(e) => onChange(e)}
					/>
					{!user.username && submitClicked && (
						<span className="error-item">Vui lòng nhập tài khoản</span>
					)}
					<p className="input-text">Mật khẩu</p>
					<input
						type="password"
						className="color-transparent"
						name="password"
						value={user.password}
						onChange={(e) => onChange(e)}
					/>
					{!user.password && submitClicked && (
						<span className="error-item">Vui lòng nhập mật khẩu</span>
					)}
					<p className="input-text">Nhập lại mật khẩu</p>
					<input
						type="password"
						className="color-transparent"
						name="passwordConfirm"
						value={user.passwordConfirm}
						onChange={(e) => onChange(e)}
					/>
					{(!user.passwordConfirm ||
						user.password != user.passwordConfirm) &&
						submitClicked && (
							<span className="error-item">
								Nhập lại mật khẩu không trùng khớp
							</span>
						)}
					<p className="input-text">Giới tính</p>
					<div className="row pl-3">
						<div className="form-check col-6">
							<input
								className="form-check-input color-transparent"
								value="male"
								type="radio"
								name="gender"
								id="male"
								checked={`${isFemale() ? '' : 'checked'}`}
								onChange={(e) => onChange(e)}
							/>
							<label
								className="form-check-label radio-for"
								htmlFor="male"
							>
								Nam
							</label>
						</div>
						<div className="form-check col-6">
							<input
								className="form-check-input color-transparent"
								value="female"
								type="radio"
								name="gender"
								id="female"
								checked={`${isFemale() ? 'checked' : ''}`}
								onChange={(e) => onChange(e)}
							/>
							<label
								className="form-check-label radio-for"
								htmlFor="female"
							>
								Nữ
							</label>
						</div>
					</div>
				</div>

				<input
					type="submit"
					className="btn-register w-100"
					value="Đăng ký"
					disabled={accountRegisterState.isLoading ? 'disable' : ''}
				></input>

				<Link className="text-center d-block mx-auto" to="dang-nhap">
					Quay lại trang đăng nhập
				</Link>
			</form>
		</div>
	);
}
