import axios from 'axios';
import { urlStr } from '../common/constants';

const userService = {
	authenticate: async (id) => {
		const res = await axios.get(`${urlStr}/accounts/authenticate`);
		return res.data;
	},
	login: async (account) => {
		const res = await axios.post(`${urlStr}/accounts/login`, account);
		return res.data;
	},
	register: async (account) => {
		const fd = new FormData();
		for (const [name, value] of Object.entries(account)) {
			fd.append(name, value);
		}

		// if (name === 'category' || name === 'brand') {
		// 	fd.append(name, value._id);
		// } else if (name === 'parameter') {
		// 	fd.append(name, JSON.stringify(value));
		// } else if (name === 'fileUpload') {
		// 	// eslint-disable-next-line no-loop-func
		// 	[...value].forEach((it, i) => {
		// 		fd.append(name + (i + 1), it.val);
		// 	});
		// } else {
		// 	fd.append(name, value);
		// }

		const res = await axios.post(`${urlStr}/accounts/register`, fd);
		return res.data;
	},
	registerVerify: async (data) => {
		try {
			const res = await axios.get(
				`${urlStr}/accounts/createaccountverify?userId=${data.userId}&code=${data.code}`
			);

			return res.data;
		} catch (error) {
			throw error.response.data;
		}
	},
};

export default userService;
