import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import userService from './../../services/userService';

import './RegisterVerify.css';

export default function RegisterAccountVerify(props) {
	const query = new URLSearchParams(window.location.search);
	const data = {
		userId: query.get('userId'),
		code: query.get('code'),
	};

	const [state, setState] = useState({
		isSuccessed: false,
		isLoading: true,
		errors: [],
	});

	useEffect(() => {
		userService
			.registerVerify(data)
			.then((res) => {
				if (res.isSuccessed) {
					setState({
						isSuccessed: true,
						isLoading: false,
						errors: [],
					});
				} else {
					setState({
						isSuccessed: false,
						isLoading: false,
						errors: [res.message],
					});
				}
			})
			.catch((error) => {
				setState({
					isSuccessed: false,
					isLoading: false,
					errors: [error.message],
				});
			});
	}, []);

	return (
		<div className="view-register-verify">
			<div className="view-register-verify__content">
				<h1 className="content-title">Nông Sản Xanh</h1>

				<div className="content-body">
					<h2>Xác thực tài khoản</h2>
					{state.errors.length > 0 &&
						state.errors.map((item, i) => (
							<h4 className="lead" key={i}>
								{item}
							</h4>
						))}

					{state.isSuccessed && (
						<div>
							<p className="lead">Bạn đã xác nhận thành công</p>
						</div>
					)}
					<Link
						to="/dang-nhap"
						className="bt-go-back btn btn-outline-primary"
					>
						Đi đến trang đăng nhập
					</Link>
				</div>

				<div className="content-footer">
					<p>&copy; 2021 NongSanXanh </p>
					<p>Khu phố 6, P.Linh Trung, Q.Thủ Đức, TP.Hồ Chí Minh</p>
					<p>
						Điện thoại: (028) 372 51993, Ext: 113(Hệ từ xa qua mạng),
						112(Hệ chính quy)
					</p>
					<p>NongSanXanh</p>
				</div>
			</div>
		</div>
	);
}
