import React, { createContext } from 'react';

import { accountRegisterReducer } from '../../reducers/member/accountRegisterReducer';
import useAsyncReducer from '../../reducers/useAsyncReducer';
// import * as ACCOUNT_REGISTER_TYPE from '../../reducers/member/accountRegisterType';

export const AccountRegisterContext = createContext();

export default function AccountContextProvider({ children }) {
	const [accountRegisterState, dispatch] = useAsyncReducer(
		accountRegisterReducer,
		{
			isLoading: false,
			isSuccessed: false,
			errors: [],
		}
	);

	const accountRegisterContextData = {
		accountRegisterState,
		dispatch,
	};

	return (
		<AccountRegisterContext.Provider value={accountRegisterContextData}>
			{children}
		</AccountRegisterContext.Provider>
	);
}
