import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import './AdminNavbar.css';

const AdminNavbar = () => {
	const [pageSelected, setPageSelected] = useState(2);

	const getIsPageSelected = (pageIndex) => {
		if (pageSelected === pageIndex) {
			return 'isActive';
		}
		return '';
	};

	return (
		<div className="admin-navbar">
			<div class="admin-navbar__navigation">
				<ul>
					<li>
						<Link to="admin">
							<span class="icon">
								<i class="fas fa-users-cog"></i>
							</span>
							<span class="title">
								<h2>ADMIN</h2>
							</span>
						</Link>
					</li>
					<li>
						<Link
							to="admin"
							class={getIsPageSelected(2)}
							onClick={() => setPageSelected(2)}
						>
							<span class="icon">
								<i class="fas fa-home"></i>
							</span>
							<span class="title">Trang chủ</span>
						</Link>
					</li>
					<li>
						<Link
							to="test1"
							class={getIsPageSelected(3)}
							onClick={() => setPageSelected(3)}
						>
							<span class="icon">
								<i
									class="fas fa-shopping-basket"
									aria-hidden="true"
								></i>
							</span>
							<span class="title">Trang test 1</span>
						</Link>
					</li>
					<li>
						<Link
							to="test2"
							class={getIsPageSelected(4)}
							onClick={() => setPageSelected(4)}
						>
							<span class="icon">
								<i
									class="fas fa-shopping-basket"
									aria-hidden="true"
								></i>
							</span>
							<span class="title">Trang test 2</span>
						</Link>
					</li>
				</ul>
			</div>
		</div>
	);
};

export default AdminNavbar;
