import React, { useContext, useEffect, useState } from 'react';
import { Link, Redirect } from 'react-router-dom';

import { checkRoleIsValid } from './../utils/checkRoleIsValid';

import AdminNavbar from '../components/partials/admin/AdminNavbar';

import { AuthContext } from './../contexts/member/AuthContext';
import * as AUTH_TYPE from './../reducers/member/authType';

import './AdminLayout.css';
import AdminTopbar from '../components/partials/admin/AdminTopbar';
import userEvent from '@testing-library/user-event';

export default function AdminLayout({ children, roles, title }) {
	const { authState, dispatch } = useContext(AuthContext);
	const [active, setActive] = useState('');

	document.title = title;

	useEffect(() => {
		dispatch({
			type: AUTH_TYPE.SET_AUTH,
			payload: null,
		});
	}, []);

	if (authState.authLoading) {
		return (
			<div className="d-flex justify-content-center align-items-center mt-2 my-loading">
				<div className="text-center">
					<h1>Loading...</h1>
				</div>
			</div>
		);
	}

	if (!checkRoleIsValid(roles, authState.permission))
		return <Redirect to="/error-401" />;

	const onClickActive = () => {
		if (!active) {
			setActive('active');
		} else {
			setActive('');
		}
	};

	return (
		<React.Fragment>
			<div className="layout-admin">
				<AdminNavbar />

				<div className={`layout-admin__main ${active}`}>
					<AdminTopbar
						avatar={authState.user.avatar}
						active={active}
						onClickActive={onClickActive}
					></AdminTopbar>

					<div>{children}</div>
				</div>
			</div>
		</React.Fragment>
	);
}
